function renderProdcuts(){products.forEach(t=>{productsDiv.innerHTML+=`\n              <div class="col mb-5">\n              <div class="card h-100">\n                  <img class="card-img-top shop-img" src="${t.imgSrc}" alt="${t.name}" />\n                  <div class="card-body p-4">\n                      <div class="text-center">\n                          <h5 class="fw-bolder">${t.name}</h5>\n                          &euro;${t.price}\n                      </div>\n                  </div>\n                  <div class="card-footer p-4 pt-0 border-top-0 bg-transparent">\n                      <div class="text-center"><a class="btn btn-outline-dark mt-auto" onclick="addToCart(${t.id})" href="#">Add to cart</a></div>\n                  </div>\n              </div>\n          </div>\n          `})}function addToCart(t){if(cart.some(a=>a.id===t))changeNumberofUnits("plus",t);else{changeTotalItems("plus");const a=products.find(a=>a.id===t);cart.push({...a,units:1})}localStorage.setItem("cart",JSON.stringify(cart))}function changeNumberofUnits(t,a){cart=cart.map(e=>{let n=e.units;return e.id===a&&("plus"===t?(n++,changeTotalItems("plus")):"minus"===t&&(n--,changeTotalItems("minus"))),{...e,units:n}})}function changeTotalItems(t){let a=localStorage.getItem("total");"plus"===t?a++:"minus"===t&&a--,localStorage.setItem("total",a),displayTotal()}let cart,total=document.getElementById("total");const productsDiv=document.querySelector(".products");renderProdcuts(),null==localStorage.getItem("cart")?(cart=[],total.innerHTML=0):(cart=JSON.parse(localStorage.getItem("cart")),total.innerHTML=cart.length),null==localStorage.getItem("total")?localStorage.setItem("total",0):displayTotal();